function Ecard() {
    const [cartsVisibilty, setCartVisible] =
        useState(false);
    const [productsInCart, setProducts] =
        useState(
            JSON.parse(
                localStorage.getItem(
                    "shopping-cart"
                )
            ) || []
        );
    useEffect(() => {
        localStorage.setItem(
            "shopping-cart",
            JSON.stringify(productsInCart)
        );
    }, [productsInCart]);
    const addProductToCart = (product) => {
        const newProduct = {
            ...product,
            count: 1,
        };
        setProducts([
            ...productsInCart,
            newProduct,
        ]);
    };



    const onProductRemove = (product) => {
        setProducts((oldState) => {
            const productsIndex =
                oldState.findIndex(
                    (item) =>
                        item.id === product.id
                );
            if (productsIndex !== -1) {
                oldState.splice(productsIndex, 1);
            }
            return [...oldState];
        });
    };

    return (
        <div className="App">
            <ShoppingCart
                visibilty={cartsVisibilty}
                products={productsInCart}
                onClose={() =>
                    setCartVisible(false)
                }

                onProductRemove={onProductRemove}
            />
            <div className="navbar">
                <h3 className="logo">ELECTRIC SERVICES</h3>
                <button
                    className="btn shopping-cart-btn"
                    onClick={() =>
                        setCartVisible(true)
                    }>
                    <GiShoppingBag size={24} />
                    {productsInCart.length >
                        0 && (
                            <span className="product-count">
                                {
                                    productsInCart.length
                                }
                            </span>
                        )}
                </button>
            </div>
            <main>
                <h2 className="title">
                    Services
                </h2>
                <div className="products">
                    {products.map((product) => (
                        <div
                            className="product"
                            key={product.id}>
                            <img
                                className="product-image"
                                src={
                                    product.image
                                }
                                alt={
                                    product.image
                                }
                            />
                            <h4 className="product-name">
                                {product.name}
                            </h4>
                            <RatingStars
                                rating={
                                    product.rating
                                }
                            />
                            {/* <p>
                                {
                                    product.description
                                }
                            </p> */}
                            <span className="product-price">
                                {product.price}$
                            </span>
                            <div className="buttons">

                                <button
                                    className="btn"
                                    onClick={() =>
                                        addProductToCart(
                                            product
                                        )
                                    }>
                                    Add to cart
                                </button>
                            </div>
                        </div>
                    ))}
                </div>
            </main>
        </div>
    );
}

export default Ecard;